import React, { useState } from 'react';

const App = () => {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      setInputText(e.target.result);
    };

    if (file) {
      reader.readAsText(file);
    }
  };

  const handleSend = async () => {
    try {
      const response = await fetch('http://localhost:8080/greet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: inputText }),
      });

      // Asegúrate de que la respuesta sea en formato JSON
      const data = await response.json();

      // Procesa el string para extraer la información deseada
      const parsedData = parseResponse(data.data);

      // Actualiza el estado con el formato deseado
      setOutputText(parsedData);
    } catch (error) {
      console.error('Error:', error);
      setOutputText('Error al obtener la respuesta del servidor.');
    }
  };

  // Función para parsear el string y formatear la salida
  const parseResponse = (response) => {
    // Ejemplo de formato: "a: &{3000 M FF /home/ba3/Discoss1.mia}"
    const regex = /a: &\{(\d+) (\w+) (\w+) (.+)\}/;
    const match = response.match(regex);

    if (match) {
      const [_, size, unit, fit, path] = match;
      const formattedSize = `${size} bytes`;
      const formattedPath = path.replace(/\/home\/ba3\//, '');

      return `Tamaño: ${formattedSize}\nFit: ${fit}\nPath: ${formattedPath}`;
    }

    return 'Formato de respuesta desconocido.';
  };

  return (
    <div style={{ textAlign: 'center', margin: '20px' }}>
      <h1>Proyecto 1</h1>
      <textarea
        rows="10"
        cols="50"
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />
      <br />
      <input type="file" onChange={handleFileChange} />
      <br />
      <button onClick={handleSend}>Enviar</button>
      <br />
      <textarea
        rows="10"
        cols="50"
        readOnly
        value={outputText}
      />
    </div>
  );
};

export default App;
