import React, { useState } from 'react';

function App() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleExecute = async () => {
    try {
      // Llama a tu backend en Go para analizar el comando
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ input: inputText }),
      });
      const result = await response.json();
      setOutputText(result.message); // Muestra el comentario o la salida del comando en el segundo textarea
    } catch (error) {
      console.error('Error ejecutando el comando:', error);
    }
  };

  return (
    <div className="App">
      <h1>Proyecto 1</h1>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        placeholder="Escribe tu comando o comentario aquí"
      ></textarea>
      <button onClick={handleExecute}>Ejecutar</button>
      <textarea
        value={outputText}
        readOnly
        placeholder="Aquí se mostrará el resultado"
      ></textarea>
    </div>
  );
}

export default App;
