import React, { useState } from 'react';

const App = () => {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setInputText(e.target.result);
      reader.readAsText(file);
    }
  };

  const handleSubmit = async () => {
    const response = await fetch('http://localhost:8080/greet', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: inputText }),
    });
    const data = await response.json();
    setOutputText(data.message);
  };

  return (
    <div style={{ textAlign: 'center', width: '80%', margin: 'auto' }}>
      <h1>Proyecto 1</h1>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        rows="10"
        cols="50"
        placeholder="Ingrese el comando aquí"
      />
      <br />
      <input type="file" onChange={handleFileSelect} />
      <br />
      <button onClick={handleSubmit}>Ejecutar</button>
      <br />
      <textarea
        value={outputText}
        readOnly
        rows="10"
        cols="50"
        placeholder="La información es:"
      />
    </div>
  );
};

export default App;
